mapper.insert:
INSERT INTO articletaglink (
articletaglinkid, articleuuid, tagid, createby,createdate,lastmodifydate
)
VALUES
(
#{id}, #{articleUuid}, #{tagId}, #{createBy},#{createDate},#{lastModifyDate}
)

mapper.select:
SELECT

FROM articletaglink
WHERE
articleuuid = #{articleUuid}
AND
tagid = #{tagId}

mapper.sql:
SELECT
FROM articletaglink ad WHERE 1=1
AND username = #{username}
AND ispublished = #{isPublished}
ORDER BY createdate DESC
