mapper.insert:
INSERT INTO tag (
tagid, value, description, createdate,lastmodify,type,parentid
)
VALUES
(
#{id}, #{value}, #{description},#{createDate},#{lastModifyDate},#{type},#{parentId}
)

mapper.update:
UPDATE
tag
SET
ispublished=#{isPublished}
WHERE
uuid = #{uuid}

mapper.sql:
SELECT
FROM tag ad WHERE 1=1
AND username = #{username}
AND ispublished = #{isPublished}
ORDER BY createdate DESC
