mapper.insert:
INSERT INTO user (
username, password, email,  telphone, createdate, invalid,
logintrytimes, lastloggeddate,ismale,iucode,reccode,signature,nickname
)
VALUES
(
#{username},#{password},#{email},#{telphone},#{createDate},	#{invalid},
#{loginTryTimes},#{createDate},#{lastLoggedDate},#{iuCode},	#{recCode},	#{signature},#{nickname}
)

mapper.select:
SELECT
FROM user u
WHERE iucode = #{iuCode}

SELECT

FROM user u
WHERE
username = #{username}
AND
password=  #{password}
